import pandas as pd
import glob
import os
import argparse


def consolidate(input_dir, output_file):
    # Find all CSV files generated by the AI
    extension = 'csv'
    all_filenames = [i for i in glob.glob(os.path.join(input_dir, f'*.{extension}'))]

    # Combine them into one dataframe
    combined_csv = pd.concat([pd.read_csv(f) for f in all_filenames])

    # Export to the data folder
    combined_csv.to_csv(output_file, index=False, encoding='utf-8-sig')
    print(f"Successfully consolidated {len(all_filenames)} files into {output_file}")


if __name__ == "__main__":
    parser = argparse.ArgumentParser()
    parser.add_argument("--input-dir", required=True)
    parser.add_argument("--output-file", required=True)
    args = parser.parse_args()
    consolidate(args.input_dir, args.output_file)